import { createRouter, createWebHistory } from 'vue-router'

// Admin Pages
import AdminDashboard from '@/views/Admin/AdminDashboard.vue'
import AdminUsers from '@/views/Admin/User.vue'
import AdminSettings from '@/views/Admin/Setting.vue'

// User Pages
import UserDashboard from '@/views/User/UserDashboard.vue'
import UserProfile from '@/views/User/Profile.vue'
import UserOrders from '@/views/User/Order.vue'

// Auth Pages
import Signin from '@/views/Signin.vue'
import Signup from '@/views/Signup.vue'


const routes = [
  // Auth routes
  { path: '/Signin', name: 'Signin', component: Signin },
  { path: '/Signup', name: 'Signup', component: Signup },

  // Admin routes
  {
    path: '/Admin',
    component: AdminDashboard,
    children: [
      { path: '', name: 'AdminDashboard', component: AdminDashboard },
      { path: 'users', name: 'AdminUsers', component: AdminUsers },
      { path: 'settings', name: 'AdminSettings', component: AdminSettings }
    ]
  },

  // User routes
  {
    path: '/User',
    component: UserDashboard,
    children: [
      { path: '', name: 'UserDashboard', component: UserDashboard },
      { path: 'profile', name: 'UserProfile', component: UserProfile },
      { path: 'orders', name: 'UserOrders', component: UserOrders }
    ]
  },

  // Default route
  { path: '/', redirect: '/Signin' }
]

const router = createRouter({
  history: createWebHistory(),
  routes
})

export default router
